package br.com.psoa.megasena.megasenaappimport android.content.Intentimport android.os.Handlerimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.view.animation.Animationimport android.view.animation.AnimationUtilsimport android.widget.ImageViewimport kotlinx.android.synthetic.main.activity_splash_screen.*class SplashScreen : AppCompatActivity() {    private val SPLASH_DISPLAY_LENGTH = 3500    public override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_splash_screen)        carregar();    }    private fun carregar() {        val anim = AnimationUtils.loadAnimation(this, R.anim.splash_animation)        anim.reset()        //Pegando o nosso objeto criado no layout        //val iv = findViewById<View>(R.id.splash) as ImageView        if (splash != null) {            splash.clearAnimation()            splash.startAnimation(anim)        }        Handler().postDelayed({            // Após o tempo definido irá executar a próxima tela            val intent = Intent(this@SplashScreen,                    MainActivity::class.java)            intent.flags = Intent.FLAG_ACTIVITY_NO_ANIMATION            startActivity(intent)            this@SplashScreen.finish()        }, SPLASH_DISPLAY_LENGTH.toLong())    }}